INSERT INTO `tblarticle` VALUES (1,'S&#7913; m&#7879;nh','T&#7853;p th&#7875; l&atilde;nh &#273;&#7841;o v&agrave; to&agrave;n th&#7875; nh&acirc;n vi&ecirc;n Mikado h&#7871;t m&igrave;nh v&igrave; s&#7921; t&#7891;n t&#7841;i v&agrave; ph&aacute;t tri&#7875;n c&#7911;a c&ocirc;ng ty v&#7899;i t&acirc;m ni&#7879;m c&ocirc;ng ty ph&aacute;t tri&#7875;n th&igrave; m&igrave;nh ph&aacute;t tri&#7875;n, c&ocirc;ng ty t&#7891;n t&#7841;i th&igrave; m&igrave;nh t&#7891;n t&#7841;i.','&lt;p style=&quot;text-align: center; &quot;&gt;&lt;img width=&quot;300&quot; height=&quot;300&quot; alt=&quot;&quot; src=&quot;/home/uploads/image/Generate/vision-mission.jpg&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;V&#7899;i s&#7913; m&#7879;nh n&amp;acirc;ng cao kh&#7843; n&#259;ng c&#7841;nh tranh c&#7911;a c&amp;ocirc;ng ty b&#7857;ng c&amp;aacute;ch t&#7841;o ra c&amp;aacute;c s&#7843;n ph&#7849;m c&amp;oacute; ch&#7845;t l&#432;&#7907;ng cao, &#7893;n &#273;&#7883;nh v&amp;agrave; li&amp;ecirc;n t&#7909;c c&#7843;i ti&#7871;n m&#7851;u m&amp;atilde; c&#361;ng nh&#432; c&#7853;p nh&#7853;t c&amp;aacute;c c&amp;ocirc;ng ngh&#7879; m&#7899;i nh&#7845;t &#273;&#7875; h&amp;agrave;i l&amp;ograve;ng kh&amp;aacute;ch h&amp;agrave;ng trong n&#432;&#7899;c, xu&#7845;t kh&#7849;u v&amp;agrave; &#273;em l&#7841;i l&#7907;i nhu&#7853;n cho c&amp;ocirc;ng ty c&#361;ng nh&#432; l&#7907;i &amp;iacute;ch c&#7911;a b&#7843;n th&amp;acirc;n.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&#7895;i c&amp;aacute; nh&amp;acirc;n &#273;&#7873;u &#273;em h&#7871;t t&amp;acirc;m huy&#7871;t c&#7911;a m&amp;igrave;nh &#273;&#7875; t&#7841;o n&amp;ecirc;n m&#7897;t t&#7853;p th&#7875; v&#7919;ng ch&#7855;c, trung th&amp;agrave;nh v&amp;agrave; t&#7853;n t&#7909;y &#273;&#7875; t&#7841;o ra m&#7897;t m&amp;ocirc;i tr&#432;&#7901;ng l&amp;agrave;m vi&#7879;c th&amp;acirc;n thi&#7879;n, hi&#7879;u qu&#7843;. S&#7913; m&#7879;nh c&#7911;a Mikado l&amp;agrave; &#273;em s&#7843;n ph&#7849;m ra ti&amp;ecirc;u th&#7909; kh&#7855;p to&amp;agrav